const fetchHandler = async () => {
   return await axios.get(URL).then((res) => res.data);

};
const Books = () => {

const [books, setBooks] = useState();


useEffect(() => {

fetchHandler().then((data) => setBooks(data.books));

}, []);

console.log(books);
  return <div>
      <ul>
        {books && books.map((book,i) => (
          <div key={i}>
         <Book book={book} />

          </div>
        ))};
      </ul>
    </div>;
  
};

export default Books;
